function addTogether() {
 var args = Array.from(arguments);
      return args.some(n => typeof n !== 'number') ?  // if args aren't numbers return undefined.
        undefined 
        : //else check if there are more than 1 args
        (args.length >= 2)
        ? // sum everything
          args.reduce((acc, n) => acc += n, 0)
          : // Else check if args are a number then sum it to args[0]
          (n) => (typeof n === "number") 
          ?  
            n + args[0]
          : // If not numbers, return undefined.
            undefined;

}

addTogether(2,3);
