function binaryAgent(str) {
  let bin2Str = "";
str.split(" ").map( (item) => bin2Str += String.fromCharCode(parseInt(item, 2)));
return bin2Str;

//fromCharCode transforms decimal numbers into its char. ParseInt transforms binary into decimal numbers. 
}

