function chunkArrayInGroups(arr, size) {
  // splice or slice?... Hm.
  let arr_copy = [];
  for (let i = 0; i < arr.length; i+=size){
    arr_copy.push(arr.slice(i, i+size)); // We just introduce the
                                         // part that we want of the array
                                         // with slice into the new array.
  }
  return arr_copy;
}



chunkArrayInGroups(["a", "b", "c", "d"], 2);
