function convertHTML(str) {
  // Create list of tags, then map the whole array getting all the keys with their item.
  const tags = {
    "&": "&amp;",
    "<": "&lt;",
    ">": "&gt;",
    "\"": "&quot;",
    "'": "&apos;",
    "¢": "&cent;",
    "£": "&pound;",
    "¥": "&yen;",
    "€": "&euro;",
    "©": "&copy;", 
    "®": "&reg;"
  }
  return str.split("").map( (item) => tags[item] || item ).join("");
  }
convertHTML("Dolce & Gabbana");
