function pairElement(str) {
  let strPair = [];

  // Split each character, then push into a new array their respective pairs.
  str.split("").filter( (item) => {
    return (item === "G") ? strPair.push(["G","C"]) : 
    (item === "C") ? strPair.push(["C","G"]) : 
    (item === "A") ? strPair.push(["A", "T"]) : strPair.push(["T", "A"]);


  })
  console.log(strPair);
  return strPair;
}

