function truthCheck(collection, pre) {
  // Is everyone being true?
 // For every item, if the item has property pre, check if truthy and return true if so.
  return collection.every( item => item.hasOwnProperty(pre) && isTruthy(item[pre]) === "truthy");
}

// Check if element is truthy or falsy.
function isTruthy(item){
  return item ? "truthy" : "falsy";
}


