function findLongestWordLength(str) {
return str.split(' ').reduce(compare,0);
// split(' ') is used to split the string into an array.
// We use reduce() to call a function that returns a single value by calling another function that goes through the whole array, 
// as if in a 'for' loop. The zero is used as "initialValue". Compare is a constant es6 function I created to compare two values and return the higher one.
}

const compare = (initialValue, nextValue) => {
return Math.max(initialValue, nextValue.length);
}
findLongestWordLength("The quick brown fox jumped over the lazy dog");
