function fearNotLetter(str) {
  // Create the whole alphabet and introduce it into an array
  let alphabet = "abcdefghijklmnopqrstuvwxyz".split("");
  // Separate each letter from the string @param.
  str = str.split(""); 
  /**
   * The starting point will be wherever the first letter of Str is in the     * alphabet. The endpoint will be that number plus the length of Str.
   */
  let startingPoint = alphabet.indexOf(str[0]);
  let endPoint = str.length + startingPoint; 
  for (let i = startingPoint; i < endPoint; i++){
    // If letter from alphabet isn't found in Str, then return that letter.
  if (str.indexOf(alphabet[i]) == -1){
  return alphabet[i];
    }
  }
  // If there is no letter returned, return undefined.
  return undefined; 
}


