let copy_arr = arr.slice();

for (let i = 0; i < copy_arr[1].length; i++){
  if (copy_arr[0].toLowerCase().indexOf(copy_arr[1][i].toLowerCase()) < 0){
    return false;
  }
}
  return true;
}

