function translatePigLatin(str) {
  let strArr = [];
  let character;

  // check if the char is consonant using RegEx
  function isConsonant(char) {
    return /^[^aeiou]/gi.test(char);
  }

  let regexVowels = /[aeiou]/gi;

  if (str.match(regexVowels) === null) return str.concat("ay");
  // return initial str + "way" if it starts with vowel
  // if not - convert str to array
  if (!isConsonant(str.charAt(0)))
    return str.concat("way");
  else
    strArr = str.split("");

  // push all consonants to the end of the array
  while (isConsonant(strArr[0])) {
    character = strArr.shift();
    strArr.push(character);
  }
  // join the array into a string, then concats the string with "ay". 
  return strArr.join("").concat("ay");
}

translatePigLatin("consonant");
