function largestOfFour(arr) {
let largest_arr = [];
for (let i = 0; i < arr.length; i++){
 arraysort(arr[i]);
 largest_arr[i] = arr[i][arr[i].length-1];
}
  return largest_arr;
}

const arraysort = (arr) => { // Bubblesort.
let temp = 0;
for (let i = 0; i < arr.length; i++){
  for (let j = 0; j < (arr.length-i); j++){
  if (arr[j-1] > arr[j]){
  temp = arr[j-1]; // Usamos la variable temporal para guardar la variable que hay que mover
  arr[j-1] = arr[j];
  arr[j] = temp;
  }
}
}
}



