function myReplace(str, before, after) {
  let regex = /[A-Z]/; // Detect uppercase.

  /*
  * Detect uppercase in before. If it is uppercase, get first character of    * after, turn it into uppercase, then sum that character with the rest of   * the string using slice at position 1 (Example: S + /itting/ ).
  * Finally, equal the string (str) to the replacement.
  */
  if (regex.test(before[0])){
  let character = after.charAt(0).toUpperCase();
  after = character + after.slice(1);
  console.log(after);
  }
  str = str.replace(before, after);
  console.log(str);
  return str;
}

myReplace("He is Sleeping on the couch", "Sleeping", "sitting");
