function titleCase(str) {

  return str.toLowerCase().replace(/(^|\s)\S/g , (letter) => letter.toUpperCase());
  // ^ -> Start of the line/string (charAt(0))
  // | -> Or
  // \s -> Space.
  // \S -> Anything not whitespace
  // g -> Global.

  // If we get it all together, it's "At the start of the string, 
  // or after a space, pick anything that isn't a whitespace 
  // in the whole string". 
  // Then we just replace that letter picked with the same letter ToUpperCase.
}

titleCase("I'm a little tea pot");
