function truncateString(str, num) {
  // Substring is a method that allow us to pick a part of the string
  // It works like this string.substring(arrayStart, arrayEnd)
  // As we can't surpass the str length we'll add a condition to detect it.
  // Or at least, it seems so.
  return (num < str.length) ? str.substring(0, num) + "..." : str;
}

truncateString("A-tisket a-tasket A green and yellow basket", 8);
