function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
 arr.sort((a,b) => a-b); // if negative number, a move to the left
                         // if positive number, a move to the right

  for (let i = 0; i < arr.length; i++) {
    if (arr[i] >= num) // You are supposed to put the number in the same
    // position of the array value that is greater than num, then pushing
    // that one and the rest one position to the right.
      return i;
  }

  return arr.length;
}

