class DisplayMessages extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: '',
      messages: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.submitMessage = this.submitMessage.bind(this);
  }
  // add handleChange() and submitMessage() methods here
  handleChange(event){
    this.setState({
      input: event.target.value
    });
  }

  submitMessage(){
    let input_state = this.state.input;
    let arr = [...this.state.messages, input_state];
    this.setState({
      input: '',
      messages: arr
    });
  }
  render() {
const msgs = this.state.messages.map((item) => (<li>{item}</li>));
    return (
      <div>
        <h2>Type in a new Message:</h2>
        { /* render an input, button, and ul here */ }
        <input type='text' onChange={this.handleChange} value={this.state.input}    placeholder="Put something in here"></input>
        <button type='button' onClick={this.submitMessage}>Click!</button>
        <ul>
        {msgs}
        </ul>
        { /* change code above this line */ }
      </div>
    );
  }
};
