const INCREMENT = "INCREMENT";
const DECREMENT = "DECREMENT"; 

const counterReducer = (state = 0, action) => {
switch (action.type){
  case INCREMENT:
  return state + 1;
  break;
  case DECREMENT:
  return state - 1;
  break;
  default:
  return state;
}
}

const incAction = () => {
  return {
    type: INCREMENT
  }
}

const decAction = () => {
  return {
    type: DECREMENT
  }
}

const store = Redux.createStore(counterReducer); // define the Redux store here, passing in your reducers
